import React,{useState} from 'react';
import PropTypes from 'prop-types';
import Slick from 'react-slick';

import styled, { createGlobalStyle } from 'styled-components';


const Overlay = styled.div`
    position: fixed;
    z-index:5000;
    top:0,
    right:0,
    bottom:0;
`;

const Header = styled.header`
    height: 44px;
    background: white;
    position:relative;
    padding:0;
    text-align:center;

    & h1{
        margin:0;
        font-size: 17px,
        color: #333,
        line-height: 44px;
    }


`

// antd 컴포넌트를 스타일링 하는 경우
const CloseBtn = styled(CloseOutlined)`
    position: absolute;
    right:0;
    top: 0;
    padding: 15px;
    line-height: 15px;
    cursor: pointer;
`





const SlickWrapper = styled.div`

    height: calc(100% -44px);
    background:#090909;


`

// Slick을 가져와 사용할 때 발생하는 문제들을 해결한다.
// Slick을 가져오면 그 안에는 이미 여려 겹의 class들이 들어있어 
//커스텀 스타일링 하는데 어려움이 있다 . 이때 Global을 사용하면 그 문제를 해결할 수 있다. 
const Global = createGlobalStyle`
    .slick-slide{
        display: inline-block;
    }
`;


const ImagesZoom = ({ images, onClose })=>{
    const [currentSlide,setCurrentSlide] =useState(0);

    return(
        <Overlay>
            <Global />
            <Header>
                <h1>상세이미지</h1>
                <CloseBtn onClick={onClose}>X</CloseBtn>
            </Header>

            <SlickWrapper>
                <div>
                    <Slick 
                        initialSlide={0}
                        // 현재 슬라이드가 몇 인지, state에 저장해놔야 한다. 
                        afterChange={(slide)=>setCurrentSlide(slide)}
                        infinite
                        arrows={false}
                        slidesToScroll={1}
                    >
                        {images.map((v)=>(
                            <Imagewrapper key={v.src}>
                                <img src={v.src} alt={v.src} />
                            </Imagewrapper>
                        ))}


                    </Slick>

                </div>

            </SlickWrapper>
        </Overlay>
    )
}


ImagesZoom.propType ={
    images:PropTypes.arrayOf(PropTypes.object).isRequired,
    onClose: PropTypes.func.isRequired
};

export default ImagesZoom;
