import React,{useCallback, useState} from 'react';
import PropTypes from 'prop-types'
import {PlusOutlined} from 'antd'

const PostImages = ({images}) =>{


    const [showImagesZoom, setShowImagesZoom] = useState(false);

    const onZoom = useCallback(()=>{
        setShowImagesZoom(true);
    },[])


    if(images.length === 1){
        return(
            <>
                <img role="presentation" src={images[0].src} art={images[0].src} onClick={onZoom} />
            </>
        )
    }


    if(images.length === 2){
        <>
            <img role="presentation" width="50%" src={images[0].src} art={images[0].src} onClick={onZoom} />
            <img role="presentation" width="50%" src={images[1].src} art={images[1].src} onClick={onZoom} />
        </>

    }

    return(
        // 이미지가 3개 이상일 때 
       <div>
            <img role="presentation" width="50%" src={images[0].src} art={images[0].src} onClick={onZoom} />
            <div
                role="presentation"
                style={{ display:'inline-block',width:'50%',textAlign:'center', verticalAlign:'middle'}}
                onClick={onZoom}

            >
                <PlusOutlined />
                <br />
                {images.length -1}
                개의 사진 더보기

            </div>
       </div>
    )

};

PostImages.prototype = {
    images:PropTypes.arrayOf(PropTypes.object),
}

export default PostImages;
