import React from 'react';
import {useSelector} from 'react-redux'
import AppLayout from '../components/AppLayout';

const Home = ()=>{

    //store state에서  login정보 가져오기
    const {isLoggedIn} = userSelector((state)=>state.user);

    //store state에서  post정보 가져오기 
    const [mainPost] = useSelector((state)=> state.post);
    return (

        // AppLayout으로 감싸지는 것들이 children이 된다.
        <AppLayout>


           {isLoggedIn && <PostForm />}

           {mainPost.map((post)=> <PostCard post={post} key={post.id}/>)} 

        </AppLayout>
    )
}

export default Home;
